: </span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">?&gt;<br /><span class="line">124: </span></span><br /><span class="line">125: </span>&nbsp;&nbsp;&nbsp;&nbsp;&lt;form&nbsp;action=""&nbsp;method="post"&gt;<br /><span class="line">126: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="text"&nbsp;name="name"&nbsp;placeholder="名前"<br /><span class="line">127: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value='<span style="color: #0000BB">&lt;?php&nbsp;</span><span style="color: #007700">if(!empty(</span><span style="color: #0000BB">$_POST</span><span style="color: #007700">[</span><span style="color: #DD0000">"edit"</span><span style="color: #007700">])){echo&nbsp;</span><span style="color: #0000BB">$name_edit</span><span style="color: #007700">;}</span><span style="color: #0000BB">?&gt;</span>'&gt;&lt;br&gt;<br /><span class="line">128: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="text"&nbsp;name="comment"&nbsp;placeholder="コメント"<br /><span class="line">129: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value='<span style="color: #0000BB">&lt;?php&nbsp;</span><span style="color: #007700">if(!empty(</span><span style="color: #0000BB">$_POST</span><span style="color: #007700">[</span><span style="color: #DD0000">"edit"</span><span style="color: #007700">])){echo&nbsp;</span><span style="color: #0000BB">$comment_edit</span><span style="color: #007700">;}</span><span style="color: #0000BB">?&gt;</span>'&gt;&lt;br&gt;<br /><span class="line">130: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="password"&nbsp;name="pass_nc"&nbsp;placeholder="パスワード"&gt;<br /><span class="line">131: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="submit"&nbsp;name="submit"&gt;&lt;br&gt;<br /><span class="line">132: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="hidden"&nbsp;name="hide_num"<br /><span class="line">133: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value='<span style="color: #0000BB">&lt;?php&nbsp;</span><span style="color: #007700">if(!empty(</span><span style="color: #0000BB">$_POST</span><span style="color: #007700">[</span><span style="color: #DD0000">"edit"</span><span style="color: #007700">])){echo&nbsp;</span><span style="color: #0000BB">$hide_num_edit</span><span style="color: #007700">;}</span><span style="color: #0000BB">?&gt;</span>'&gt;<br /><span class="line">134: </span>&nbsp;&nbsp;&nbsp;&nbsp;<br /><span class="line">135: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;<br /><span class="line">136: </span>&nbsp;&nbsp;&nbsp;&nbsp;<br /><span class="line">137: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="number"&nbsp;name="delete"&nbsp;placeholder="削除対象番号"&gt;&lt;br&gt;<br /><span class="line">138: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="password"&nbsp;name="pass_del"&nbsp;placeholder="パスワード"&gt;<br /><span class="line">139: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="submit"&nbsp;name="submit"&nbsp;value="削除"&gt;&lt;br&gt;<br /><span class="line">140: </span><br /><span class="line">141: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;<br /><span class="line">142: </span>&nbsp;&nbsp;&nbsp;&nbsp;<br /><span class="line">143: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="number"&nbsp;name="edit"&nbsp;placeholder="編集対象番号"&gt;&lt;br&gt;<br /><span class="line">144: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="password"&nbsp;name="pass_edit"&nbsp;placeholder="パスワード"&gt;<br /><span class="line">145: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type="submit"&nbsp;name="submit"&nbsp;value="編集"&gt;&lt;br&gt;<br /><span class="line">146: </span>&nbsp;&nbsp;&nbsp;&nbsp;&lt;/form&gt;<br /><span class="line">147: </span>&nbsp;&nbsp;&nbsp;&nbsp;<br /><span class="line">148: </span>&nbsp;&nbsp;&nbsp;&nbsp;&lt;hr&nbsp;width="40%"&nbsp;align="left"&gt;<br /><span class="line">149: </span>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #0000BB">&lt;?php<br /><span class="line">150: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">if(!empty(</span><span style="color: #0000BB">$errorpass</span><span style="color: #007700">)){<br /><span class="line">151: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #0000BB">$errorpass</span><span style="color: #007700">.</span><span style="color: #DD0000">"&lt;br&gt;"</span><span style="color: #007700">;<br /><span class="line">152: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{<br /><span class="line">153: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"&lt;br&gt;"</span><span style="color: #007700">;<br /><span class="line">154: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br /><span class="line">155: </span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">?&gt;<br /><span class="line">156: </span></span>&nbsp;&nbsp;&nbsp;&nbsp;&lt;hr&nbsp;width="40%"&nbsp;align="left"&gt;<br /><span class="line">157: </span><br /><span class="line">158: </span>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: #0000BB">&lt;?php<br /><span class="line">159: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$sql&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'SELECT&nbsp;*&nbsp;FROM&nbsp;mission5'</span><span style="color: #007700">;<br /><span class="line">160: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$stmt&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$pdo</span><span style="color: #007700">-&gt;</span><span style="color: #0000BB">query</span><span style="color: #007700">(</span><span style="color: #0000BB">$sql</span><span style="color: #007700">);<br /><span class="line">161: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$results&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$stmt</span><span style="color: #007700">-&gt;</span><span style="color: #0000BB">fetchAll</span><span style="color: #007700">();<br /><span class="line">162: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;(</span><span style="color: #0000BB">$results&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #0000BB">$row</span><span style="color: #007700">){<br /><span class="line">163: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//$rowの中にはテーブルのカラム名が入る<br /><span class="line">164: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">echo&nbsp;</span><span style="color: #0000BB">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'id'</span><span style="color: #007700">].</span><span style="color: #DD0000">'&nbsp;'</span><span style="color: #007700">;<br /><span class="line">165: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #0000BB">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'name'</span><span style="color: #007700">].</span><span style="color: #DD0000">'&nbsp;'</span><span style="color: #007700">;<br /><span class="line">166: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #0000BB">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'comment'</span><span style="color: #007700">].</span><span style="color: #DD0000">'&nbsp;'</span><span style="color: #007700">;<br /><span class="line">167: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #0000BB">$row</span><span style="color: #007700">[</span><span style="color: #DD0000">'date'</span><span style="color: #007700">].</span><span style="color: #DD0000">'&lt;br&gt;'</span><span style="color: #007700">;<br /><span class="line">168: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"&lt;hr&gt;"</span><span style="color: #007700">;<br /><span class="line">169: </span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br /><span class="line">170: </span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">?&gt;<br /><span class="line">171: </span></span>&lt;/body&gt;<br /><span class="line">172: </span>&lt;/html&gt;</span>
</code><br />    </body>
    <script>
        let checkbox = document.getElementById('checkbox');
        checkbox.addEventListener('change', valueChange);
        function valueChange(){
            lines=document.getElementsByClassName('line')
            if((document.getElementById('checkbox').checked)){
                Array.prototype.forEach.call(lines, function(line) {
                    line.style.visibility ="visible"
                })
            }else{
                Array.prototype.forEach.call(lines, function(line) {
                    line.style.visibility ="hidden"
                })
            }
        }
        </script>
</html>